mapper.select:
SELECT * FROM (
    SELECT A.*, ROWNUM AS RNUM FROM (
        SELECT notice_id, title, content, user_id, created_at, updated_at, view_count 
        FROM notice 
        ORDER BY created_at DESC
    ) A WHERE ROWNUM <= #{endRow}
) WHERE RNUM > #{startRow}

SELECT notice_id, title, content, user_id, created_at, updated_at, view_count 
FROM notice 
WHERE notice_id = #{noticeId}
