mapper.update:
UPDATE users
SET role = #{role}
WHERE user_id = #{userId}

UPDATE users
SET status = #{status}
WHERE user_id = #{userId}

mapper.select:
SELECT user_id AS userId, username AS userName, email, tel, nickname
FROM users
WHERE user_id = #{userId}

mapper.update:
UPDATE users SET username = #{userName}, email = #{email}, tel = #{tel},
password = #{password},
WHERE user_id = #{userId}
